import { useState } from "react"

const Tasks = () => {
    const [tasks, setTasks] = useState([
        {
            id: 1,
            text: 'some text',
            day: '5th of feb',
            reminder: true,
        },
        {
            id: 2,
            text: 'some text2',
            day: '6th of feb',
            reminder: false,
        },
        {
            id: 3,
            text: 'some text3',
            day: '7th of feb',
            reminder: true,
        },
    ])

  return (
    <>
      {tasks.map((task)=>(<h3 key={task.id}>{task.text}</h3>))}
    </>
  )
}

export default Tasks
