const tasks = [
    {
        id: 1,
        text: 'some text',
        day: '5th of feb',
        reminder: true,
    },
    {
        id: 2,
        text: 'pizza',
        day: '7th of Augh',
        reminder: false,
    },
    {
        id: 3,
        text: 'popcorn',
        day: '8th of Sept',
        reminder: true,
    },
]

const Tasks = () => {
  return (
    <>
      {tasks.map((task)=>(<h3 key={task.id}>{task.text}</h3>))}
    </>
  )
}

export default Tasks
