  const [tasks, setTasks] = useState([
    {
        id: 1,
        text: 'some text',
        day: '5th of feb',
        reminder: true,
    },
    {
        id: 2,
        text: 'some text2',
        day: '6th of feb',
        reminder: false,
    },
    {
        id: 3,
        text: 'some text3',
        day: '7th of feb',
        reminder: true,
    },
])

// Add Task
const addTask = (task) => {
  const id = Math.floor(Math.random() * 10000) + 1
  console.log(id)
  const newTask = {id, ...task};
  setTasks([...tasks, newTask])
}

// Delete Task
const deleteTask = (id) => {
  setTasks(tasks.filter((task) => task.id !==id))
}

// Toggle Reminder
const toggleReminder = (id) => {
  setTasks(tasks.map((task) => task.id===id
  ? {...task, reminder : !task.reminder}
  : task))
}

  return (
    <div className='container'>
      <Header />
      <AddTask onAdd={addTask} />
      {tasks.length > 0 ?
        <Tasks tasks={tasks}
        onDelete={deleteTask}
        onToggle={toggleReminder}
        />
      : 'No Tasks To Show'}
    </div>
  );
}

